import { useState } from "react";

function Home() {
  return (
    <div>
      <Contador />
    </div>
  );
}

function Contador() {
  const [contador, setContador] = useState(1);

  function adicionarContador() {
    setContador(setContador + 1);
  }

  return (
    <div>
      <div>{this.state.contador}</div>
      <button onClick={adicionarContador}>Adicionar contador</button>
    </div>
  );
}

export default Home;
